class StickyCTS{constructor(){this.stickyPositions=[],this.containerBoxes=[],this.comparisonElements=[],this.getStickyCTSPositions(),this.getContainerBoxes(),this.addComparisonElements(),this.setStickyToPositions()}getStickyCTSPositions(){this.stickyPositions=document.querySelectorAll(".line.cts.sticky")}getContainerBoxes(){this.stickyPositions.length&&this.stickyPositions.forEach(t=>{const s=StickyCTS.getParentBoxOfCTS(t);s.setAttribute("style","width: "+s.getBoundingClientRect().width+"px"),this.containerBoxes.push(s)})}static getParentBoxOfCTS(t){let s=t.parentNode;for(;-1===s.className.split(" ").indexOf("box");)s=s.parentNode;return s}addComparisonElements(){this.containerBoxes.length&&this.containerBoxes.forEach(t=>{const s=document.createElement("div");s.className="comparison-element",t.parentNode.insertBefore(s,t),this.comparisonElements.push(s)})}setStickyToPositions(){this.comparisonElements.length&&window.addEventListener("scroll",t=>{this.comparisonElements.forEach((t,s)=>{t.getBoundingClientRect().top<=153?-1===this.containerBoxes[s].className.split(" ").indexOf("sticky")&&(this.containerBoxes[s].className=this.containerBoxes[s].className.trim()+" sticky"):-1!==this.containerBoxes[s].className.split(" ").indexOf("sticky")&&(this.containerBoxes[s].className=this.containerBoxes[s].className.split(" ").map(t=>{if(""!==t&&"sticky"!==t)return t}).join(" ").trim())})})}}new StickyCTS;
