'use strict';(()=>{const outerHeight=(el)=>{var height=el.offsetHeight;var style=getComputedStyle(el);height+=parseInt(style.marginTop)+parseInt(style.marginBottom);return height};const headerSticky=()=>{const $headerTop=document.querySelector('.main-header__header-top'),$mainHeader=document.querySelector('header.main-header');const mainHeaderHeight=$mainHeader.offsetHeight;if(!$headerTop)return;document.body.style.setProperty('--header-height',mainHeaderHeight+'px');const $body=document.body,$headerTopHeight=outerHeight($headerTop);const toggleClass=()=>{if(document.documentElement.scrollTop>$headerTopHeight&&!$body.classList.contains('body--header-sticky')){$body.classList.add('body--header-sticky');document.body.style.setProperty('--header-height',$headerTopHeight+'px')}else if(document.documentElement.scrollTop<$headerTopHeight&&$body.classList.contains('body--header-sticky')){$body.classList.remove('body--header-sticky');document.body.style.setProperty('--header-height',mainHeaderHeight+'px')}};let scroll=function(){toggleClass()};const raf=window.requestAnimationFrame||window.webkitRequestAnimationFrame||window.mozRequestAnimationFrame||window.msRequestAnimationFrame||window.oRequestAnimationFrame;let lastScrollTop=document.documentElement.scrollTop;let loop=()=>{let scrollTop=document.documentElement.scrollTop;if(lastScrollTop===scrollTop){raf(loop);return}else{lastScrollTop=scrollTop;scroll();raf(loop)}};if(raf){loop()}};const ready=(fn)=>{if(document.readyState!='loading'){fn()}else{document.addEventListener('DOMContentLoaded',fn)}};setTimeout(()=>{ready(headerSticky)},500)})()