function ocultarChatBotGeneral() {
	if (window.mobileAndTabletcheck()) {
		ocultarChatBot();
	} else {
		var idSetIntervalXms = setInterval(function() {
			if (esLoginUsuarioChatBot || contX >= 1001){
				clearInterval(idSetIntervalXms);
			} else  {
				setloginChatBot();
			}
		}, 100);
	}
	sobreEscribirPosicionWidget();
	sobreEscribirColorWidget();
	datalayerCancelarbtn();
	datalayerIniciarChatbtn();
	sobreEscribirColorTextWidget();
	sobreEscribirColorIconWidget();
}

function ocultarChatBot() {
	var idSetIntervalX = setInterval(function() {
		try {
			var show = false;
			var x = document.getElementsByClassName("cx-widget cx-common-container cx-webchat cx-close cx-minimize cx-theme-blue cx-transcript-view cx-mobile cx-portrait");
			if (x && x.length > 0) {
				show = true;
			}
			var x = document.getElementsByClassName("cx-widget cx-common-container cx-webchat cx-close cx-minimize cx-theme-blue cx-mobile cx-transcript-view cx-portrait");
			if (x && x.length > 0) {
				show = true;
			}
			var x = document.getElementsByClassName("cx-widget cx-common-container cx-webchat cx-close cx-minimize cx-theme-blue cx-mobile cx-transcript-view cx-landscape");
			if (x && x.length > 0) {
				show = true;
			}
			var x = document.getElementsByClassName("cx-widget cx-common-container cx-webchat cx-close cx-minimize cx-theme-blue cx-transcript-view cx-desktop");
			if (x && x.length > 0) {
				show = true;
			}
			if (show) {
				forzarMinimizarChatBot();
				clearInterval(idSetIntervalX);
			}
			setloginChatBot();
		} catch (exception) {
			console.error(exception);
		}
	}, 100);
}

function forzarMinimizarChatBot() {
	var idSetIntervalXxx = setInterval(function() {
		try {
			var checkMobile = document.getElementsByClassName("cx-icon cx-button-minimize");
			var hide = isHideChatBot();
			if (checkMobile && checkMobile.length > 0 && !hide) {
				if (!esLoginUsuarioChatBot) {
					checkMobile[0].click();
					var xsmx = document.getElementsByClassName("cx-widget cx-side-button-group");
					if (xsmx && xsmx.length > 0) {
						xsmx[0].style.display = "none";	
						clearInterval(idSetIntervalXxx);
					}
				}
			}
			if (hide){
				clearInterval(idSetIntervalXxx);
			}
		} catch (exception) {
			console.error(exception);
		}
	}, 100);
}

