var setBoxHeights = function (className) {
  var pheight = 0;
  $(className).css('min-height', pheight);

  $(className).each(function () {
    if ($(this).height() > pheight) {
      pheight = $(this).height();

    }
  });

  $(className).css('min-height', pheight);
};

var menuWidthCheck = function () {
  if (!mmgenerated) {
    mmenu = $("#menu").clone();
  }

  if (menubutton.is(":visible")) {
    if (!mmgenerated) {
      menubutton.attr('href', '#menu');

      mmenu.find('.active').addClass('Selected');
      mmenu.mmenu({
        slidingSubmenus: false
      }, {
        clone: true
      });

      mmAPI = mmenu.data("mmenu");

      mmgenerated = true;
    }
    mobilemenu = true;

  } else if (jQuery('html').hasClass('mm-opened')) {
    mmAPI.close();

    mobilemenu = false;


  } else {
    mobilemenu = false;
  }

  if (!navsetup && !mobilemenu) {
    new navigationSetup();
    navsetup = true;
  }
}

