$.fn.preload = function () {
  this.each(function () {
    $('<img/>')[0].src = this;
  });
}

var searchBarSticky = function () {
  var scrolltop = ($(document).scrollTop());
  var topofshadowbox = ($('div.shadowbox').offset().top);

  if (scrolltop > topofshadowbox && selectfilterfixed == 0) {
    selectfilter.addClass('selectfilterfixed'); //.fadeTo(300, 0.9);
    selectfilterfixed = 1;
    $('div.shadowbox').css('padding-top', (selectfilter.height() + 2));

  } else if (scrolltop <= topofshadowbox && selectfilterfixed == 1) {
    selectfilter.removeClass('selectfilterfixed'); //.fadeTo(300, 1);
    selectfilterfixed = 0;
    $('div.shadowbox').css('padding-top', 2);
  }
};

var fancyboxhistorybackChange = function (tf) {
  if (tf == 1) {
    window.parent.fancyboxhistoryback = true;
    $('#closeback', window.parent.document).addClass('fancybox-back').attr('title', 'Back');
  } else {
    window.parent.fancyboxhistoryback = false;
    $('#closeback', window.parent.document).removeClass('fancybox-back').attr('title', 'Close');
  }
};

var jcarouselSetup = function (id, listid, initialnum) {
  var owl = $("#" + id);

  if (initialnum == 3) {
    sizes = {
      0: {
        items: 1
      },
      600: {
        items: 2
      },
      800: {
        items: 3
      }
    };
  } else {
    sizes = {
      0: {
        items: 1
      },
      400: {
        items: 2
      },
      600: {
        items: 3
      },
      800: {
        items: 4
      }
    };
  }

  owl.owlCarousel({
    loop: false,
    callbacks: true,
    items: initialnum,
    responsive: sizes,
    onInitialized: function () {
      //new setBoxHeights('div.'+id+'inner');
    },
    onResized: function () {
      //new setBoxHeights('div.'+id+'inner');
    }
  });

  // Custom Navigation Events
  $("#" + id + "next").click(function () {
    owl.trigger('next.owl.carousel');
  });
  $("#" + id + "prev").click(function () {
    owl.trigger('prev.owl.carousel');
  });
};

var jcarouselSetupOld = function (id, listid) {
  $('#' + id).jcarousel({
    initCallback: function (carousel) {

      if (listid != 0) {
        $('#regions' + listid + ' a').bind('click', function () {
          carousel.scroll($.jcarousel.intval($(this).attr('data-pos')));
          return false;
        });
      }
    }
  });

  carousels[id] = $('#' + id).data('jcarousel');
};

var itinerarySearchSetup = function () {
  $('#searchfiltergo').click(function (e) {
    e.preventDefault();

    searchfiltervalue = $('#searchfilter').val();

    new itinerarySearchFilter();
  });

  $('#searchfilter').keypress(function (e) {
    if (e.which == 13) {
      e.preventDefault();

      searchfiltervalue = $('#searchfilter').val();

      new itinerarySearchFilter();
    }
  });
};

var itinerarySearchFilter = function () {
  new itinerarySearchShowHide(1);
  new itinerarySearchShowHide(2);
  new itinerarySearchShowHide(5);

  if (searchfiltervalue != '' && !searchfilterreset) {
    searchfilterreset = $('<a></a>').attr('href', 'javascript:;').text('Clear').click(function (event) {
      event.preventDefault();

      $('#searchfilter').val('');
      searchfiltervalue = '';
      new itinerarySearchFilter();

      $(this).remove();
      searchfilterreset = null;
    });

    $('#searchfiltergo').after(searchfilterreset);
  }
};

var itinerarySearchShowHide = function (id) {
  $('#collectionlist' + id + ' li.searchable').each(function () {
    if (searchfiltervalue != '') {
      var keywords = $(this).attr('data-keywords').toLowerCase();

      if (keywords.indexOf(searchfiltervalue.toLowerCase()) > -1) {
        if ($(this).hasClass('hidden')) {
          $(this).parent().show();
          $(this).removeClass('hidden');
        }

      } else if (!$(this).hasClass('hidden')) {
        $(this).parent().hide();
        $(this).addClass('hidden');
      }
    } else if ($(this).hasClass('hidden')) {
      $(this).parent().show();
      $(this).removeClass('hidden');
    }
  });

  $('#collectionlist' + id).trigger('to.owl.carousel', [0]);
};

var preloadFunc = function () {
  var preloadsss = Array();
  $.each(photos, function (key, value) {
    preloadsss[key] = baseurl + value.crop + folderurl + value.image;
  });

  $(preloadsss).preload();
}

