var days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

var calculateDates = function () {
  var startdate = $('#startdate').val();

  if (startdate != '') {
    var startdateArr = startdate.split('-');
    var actualdate = new Date(startdateArr[2], parseInt(startdateArr[1] - 1), parseInt(startdateArr[0]));

    $('#itinerarylist > li').each(function () {
      $(this).find('input.date').val(days[actualdate.getDay()] + ' ' + String("00" + actualdate.getDate()).slice(-2) + '-' + String("00" + (actualdate.getMonth() + 1)).slice(-2) + '-' + actualdate.getFullYear());
      if ($(this).attr('data-calcnights') == '1') {
        //var incrementdays=((parseInt($(this).attr('data-type'))==5 && parseInt($(this).attr('data-days'))>1) ? parseInt($(this).attr('data-days')) : 1);
        var incrementdays = ((jQuery.inArray(parseInt($(this).attr('data-type')), stayArray) > -1 && parseInt($(this).attr('data-days')) > 1) ? parseInt($(this).attr('data-days')) : 1);

        actualdate.setDate(actualdate.getDate() + incrementdays);
      }
    });
  }
};

