var selectionChange = function (disableRI) {
  var types = [];
  var regions = [];
  var interests = [];

  $("#typecheckboxes input:checkbox:checked").each(function () {
    types.push(parseInt($(this).val()));
  });

  if (!disableRI) {
    $("#regioncheckboxes input:checkbox:checked").each(function () {
      regions.push(parseInt($(this).val()));
    });

    $("#interestcheckboxes input:checkbox:checked").each(function () {
      interests.push(parseInt($(this).val()));
    });
  } else {

    var ininterest = true;
    var inregion = true;
  }

  $('#selectitems ul li.item').each(function () {
    var inarray = false;

    if (jQuery.inArray(parseInt($(this).attr('data-type')), types) > -1) {
      inarray = true;

      if (disableRI) {
        $(this).fadeIn().removeClass('hidden');

        if ($(this).hasClass('hidden')) {
          $(this).fadeIn().removeClass('hidden');
        }

      } else {
        var testregions = $(this).attr('data-regions').split(',');

        var inregion = false;
        for (i = 0; i < testregions.length; i++) {
          if (jQuery.inArray(parseInt(testregions[i]), regions) > -1) {
            inregion = true;
          }
        }

        if (inregion) {
          var testinterests = $(this).attr('data-interests').split(',');

          var ininterest = false;
          for (i = 0; i < testinterests.length; i++) {
            if (jQuery.inArray(parseInt(testinterests[i]), interests) > -1) {
              ininterest = true;
            }
          }

          if (ininterest) {
            $(this).fadeIn().removeClass('hidden');

            if ($(this).hasClass('hidden')) {
              $(this).fadeIn().removeClass('hidden');
            }
          } else {
            $(this).hide().addClass('hidden');
          }

        } else {
          $(this).hide().addClass('hidden');
        }
      }
    }

    if (searchfiltervalue != '') {
      var keywords = $(this).attr('data-keywords').toLowerCase();

      if (keywords.indexOf(searchfiltervalue.toLowerCase()) == -1) {
        if (!$(this).hasClass('hidden')) {
          $(this).hide().addClass('hidden');
        }
      }
    }

    if (!disableRI) {
      if (!$(this).hasClass('hidden') && !ininterest && !inregion) {
        $(this).hide().addClass('hidden');
      }
    } else {
      if (!$(this).hasClass('hidden') && !inarray) {
        $(this).hide().addClass('hidden');
      }
    }


  });

  if (displayType == 1) {
    new collectionMap('divmap');
  }

  if ($('#selectitems ul li:visible').length == 0) {
    if (!noitems) {
      noitems = $('<h2>').text('No items matching the current criteria.').addClass('noitems');
      $('#selectitems').append(noitems);
    }
  } else if (noitems) {
    noitems.remove();
    noitems = null;
  }
};

