var postForm = function (formid, url, submitid, dateid, formchangeclass, cc) {
  var form = $('#' + formid);

  //$('.chzn-select').chosen();

  if (dateid != '') {
    new datePicker(dateid);
  }

  if (cc == 1) {
    new creditCardV();
  }

  if (formchangeclass != '') {
    $('.' + formchangeclass).each(function () {
      $(this).change(function () {
        new postFormChange(formid, formid + 'inner.php', dateid, formchangeclass, cc, '');
      });
    });
  }

  submitted = false;

  form.submit(function (event) {
    form.addClass('loading');

    new scrollToDiv(form);

    $.ajax({
      type: 'POST',
      url: '/inc/' + url,
      cache: false,
      data: form.serialize(),
      success: function (response) {
        form.html(response);
        form.removeClass('loading');

        //$('.chzn-select').chosen();
        $("select.uniform, input.input, input.uniform, input.submit, textarea.input").uniform();

        if (dateid != '') {
          new datePicker(dateid);
        }

        if (cc == 1) {
          new creditCardV();
        }

        if (formchangeclass != '') {
          $('.' + formchangeclass).each(function () {
            $(this).change(function () {
              new postFormChange(formid, formid + 'inner.php', dateid, formchangeclass, cc, '');
            });
          });
        }

        submitted = false;

        if (submitid != '') {
          $('#' + submitid).click(function (event) {
            if (!submitted) {
              form.submit();
              submitted = true;
            }
            event.preventDefault();
          });
        }
      }
    });

    event.preventDefault();
  });

  if (submitid != '') {
    $('#' + submitid).click(function (event) {
      if (!submitted) {
        form.submit();
        submitted = true;
      }
      event.preventDefault();
    });
  }
};

