var collectionMap = function (mapid) {
  mapdivid = mapid;

  if (!map) {
    map = new Microsoft.Maps.Map(document.getElementById(mapid), {
      credentials: bingMapsKey,
      mapTypeId: Microsoft.Maps.MapTypeId.road,
      showDashboard: true,
      enableClickableLogo: false,
      enableSearchLogo: false
    });
  } else {
    map.entities.clear();
  }

  cluseterLayer = new ClusteredEntityCollection(map, {
    singlePinCallback: createPin,
    clusteredPinCallback: createClusteredPin
  });

  var locs = [];

  $('#selectitemslist > li').each(function () {
    if (!$(this).hasClass('hidden')) {
      id = $(this).attr('data-id');
      type = $(this).attr('data-type');
      maplat = $(this).attr('data-maplat');
      maplng = $(this).attr('data-maplng');

      name = $(this).find('div.selectheading h2').text();
      link = $(this).find('div.selectheading h2 a').attr('href');
      town = $(this).find('div.selectheading p').text();
      image = $(this).find('img').attr('src');

      if (maplat != '' && maplng != '') {
        loc = new Microsoft.Maps.Location(maplat, maplng);
        loc.Id = id;
        loc.Name = name;
        loc.Town = town;
        loc.Type = type;
        loc.Link = link;
        loc.Image = image;

        locs.push(loc);
      }
    }
  });

  infoboxLayer = new Microsoft.Maps.EntityCollection();
  pinInfobox = new Microsoft.Maps.Infobox(new Microsoft.Maps.Location(0, 0), {
    visible: false,
    width: 160
  });
  infoboxLayer.push(pinInfobox);
  map.entities.push(infoboxLayer);

  if (locs.length > 1) {
    viewBoundaries = Microsoft.Maps.LocationRect.fromLocations(locs);
    cluseterLayer.SetData(locs);
    map.setView({
      bounds: viewBoundaries
    });
  } else if (locs.length == 1) {
    locs[0]._LatLong = new Microsoft.Maps.Location(locs[0].latitude, locs[0].longitude);
    createPin(locs[0]);
  }

  $('#' + mapdivid + 'loading').css('display', 'none');
};

function createPin(data) {
  var pin = new Microsoft.Maps.Pushpin(data._LatLong, {
    id: data.Id,
    icon: '/images/marker' + data.Type + '.png',
    width: 25,
    height: 34
  });

  pin.Title = data.Name;
  pin.Description = data.Town + '<br /><a href="' + data.Link + '">View &raquo;</a><br /><img src="' + data.Image + '" />';

  Microsoft.Maps.Events.addHandler(pin, 'click', displayInfoboxCluster);

  return pin;
}

function createClusteredPin(cluster, latlong) {
  var pin = new Microsoft.Maps.Pushpin(latlong, {
    icon: '/images/markerc.png?v2',
    width: 25,
    height: 25
  });

  Microsoft.Maps.Events.addHandler(pin, 'click', zoomToCluster);

  return pin;
}

function displayInfoboxCluster(e) {
  pinInfobox.setOptions({
    width: 176,
    height: 190,
    title: e.target.Title,
    description: e.target.Description,
    visible: true,
    offset: new Microsoft.Maps.Point(0, 25)
  });
  pinInfobox.setLocation(e.target.getLocation());

  $('.Infobox a.fancyboxiframe').click(function (event) {
    event.preventDefault();

    //new fancyboxiframeClick($(this).attr('href'), '');

    /*$.fancybox.open(
    	[{href : $(this).attr('href')}],
    	{fitToView: false,padding: 4, scrolling: 'no',width: 869, height: '100%', autoSize: false, openEffect: 'none', closeEffect: 'none', type: 'iframe', helpers : {overlay : {showEarly: true, closeClick: false}}}
    );*/


  });
}

function zoomToCluster(e) {
  var zoomLevel = map.getZoom() + 1;
  map.setView({
    center: e.target.getLocation(),
    zoom: zoomLevel
  });

  hideInfobox();
}



